import React, { Component } from 'react'

export default class Coursesale extends Component {
    sumPrice(){
        this.setState({

        })
    }
    constructor(props) {
        super(props)

        this.state = {
             total: 0
        }
    }


    render() {
        var courses = this.props.items.map((item, index)=>{
            return <Course name={item.name} price={item.price} key={index} sumPrice={this.sumPrice}/>
        })
        return (
            <div>
              {courses}  
              <p>Total:{this.state.total}</p>
            </div>
        )
    }
}


class Course extends Component {
    clicker=()=>{
        console.log("you clicked me")
    }
    render() {
        return (
            <div>
                <p onClick={this.clicker}>{this.props.name}</p><b>{this.props.price}</b>

            </div>
        )
    }
}
