import React, { Component } from 'react'

export default class Coursesale extends Component {

    render() {
        var courses = this.props.items.map((item, index)=>{
            return <Course name={item.name} price={item.price} key={index}/>
        })

        return (
            <div>
              {courses}  
            </div>
        )
    }
}


class Course extends Component {
    render() {
        return (
            <div>
                <p>{this.props.name}<b>{this.props.price}</b></p>
        <p><strong>Total:</strong>{this.state.total}</p>
            </div>
        )
    }
}
