import React, { Component } from 'react'

class Coursesale extends Component {
    sumPrice(price){
        this.setState({total: this.state.total + price});
    }

    constructor(props) {
        super(props)

        this.state = {
             total: 0
        }
    }

    render() {
        var courses = this.props.items.map((item, i)=>{
            return <Course name={item.name} price={item.price} key={i} sumPrice={this.sumPrice} active={item.active}/>
        })
        return (
            <div>
                <h1>You can buy courses:</h1>
                <div id="courses">
                {courses}
        <p id="total">Total <b>{this.state.total}</b></p>
                </div>
            </div>
        )
    }
}

class Course extends Component {
    clicker(){

    }
    constructor(props) {
        super(props)

        this.state = {
             active: false
        }
    }

    render() {
        return (
            <div>
                <p onClick={this.clicker}><b>{this.props.name}</b>{this.props.price}</p>
            </div>
        )
    }
}

export default Coursesale;
